import React, { useContext, useEffect, useRef, useState } from "react";
import classes from "./SmallInput.module.css";
import CardDataContext from "../../../../Context/CardData";
import { calculateNewValue } from "@testing-library/user-event/dist/utils";

const SmallInput = (props) => {

  const ctx = useContext(CardDataContext);

  const [slipVal, setSlipVal] = useState(0);
  const item = useRef(0);
  useEffect(() => {
    setSlipVal(parseInt(props.datas.value));
  }, [props.datas.value]);

  const saveInLocalStorage = () => { // modify make it save directly in the dataSource cars 
    let dataArr = JSON.parse(localStorage.getItem('dataCars') || '[]');
    if (dataArr.length >= props.datas.index) {
      console.log(dataArr);
      const val = dataArr.includes(props.datas.index, 0);
      console.log(val);
    }
    if (dataArr[props.datas.index] === undefined) {
      console.log('save value', props.datas);
      ctx.array({ ...props.datas })
      // } else if (dataArr.length >= props.datas.index + 1) {
      //   dataArr[props.datas.index].value = item.current.value;
      //   console.log(dataArr);
      //   localStorage.setItem('dataCars', JSON.stringify(dataArr));
      //   console.log('good', dataArr);
      // }

    }
  }
    const getCountreClickPlus = () => {
      setSlipVal(prevVal => prevVal + 1);
    }
    const getCountreClickMin = () => {
      setSlipVal(prevVal => prevVal - 1);
    }
    useEffect(() => {
      saveInLocalStorage();
      if (props.valide === false) { ctx.onPurches() };
    })

    return (
      <div className={classes.counter}>
        <span onClick={getCountreClickMin}>-</span>
        <input
          className={classes.input}
          type="text"
          value={slipVal}
          readOnly
          ref={item}
        ></input>
        <span onClick={getCountreClickPlus}>+</span>
      </div>
    );
  };

  export default SmallInput;
